mapper.update:
update member
set member_password=#{password}
where member_id=#{id}

mapper.select:
select member_id, member_password, name, nickname, birth, sex, zipcode, address, detail_address, email, phone_no,
					 favorite_food, mileage, grade, join_date
from (

	select ceil(rownum/#{contentsPerPage}) page, member_id, member_password, name, nickname, birth, sex, zipcode, address, detail_address, email, phone_no,
											favorite_food, mileage, grade, join_date
	from (
			
			order by join_date
	)
) 
where page = #{pageNo}
