mapper.select:
select	restaurant_no, restaurant_name, category, phone_no, address, description,
		picture_name, score, hits, theme, reg_date, reply_count, location_no
from	(
			select	ceil(rownum/#{contentsPerPage}) page, restaurant_no, restaurant_name, category, phone_no, address,
					description, picture_name, score, hits, theme, reg_date, reply_count, location_no
			from	(
						
						
							
								category=#{category}
							
							
								and restaurant_name like '%'||#{searchWord}||'%'
							
						
						
							
								order by restaurant_name
							
							
								order by hits desc
							
							
								order by reply_count desc
							
							
								order by score desc
							
							
								order by restaurant_no desc
							
						
					)
		)
where	page = #{currentPage}

select	restaurant_no, restaurant_name, category, phone_no, address, description,
		picture_name, score, hits, theme, reg_date, reply_count, location_no
from	(
			select	ceil(rownum/#{contentsPerPage}) page, restaurant_no, restaurant_name, category, phone_no, address,
					description, picture_name, score, hits, theme, reg_date, reply_count, location_no
			from	(
						
						
							
								theme like '%'||#{theme}||'%'
							
							
								and restaurant_name like '%'||#{searchWord}||'%'
							
						
						
							
								order by restaurant_name
							
							
								order by hits desc
							
							
								order by reply_count desc
							
							
								order by score desc
							
							
								order by restaurant_no desc
							
						
					)
		)
where	page = #{currentPage}

select	restaurant_no, restaurant_name, category, phone_no, address, description,
		picture_name, score, hits, theme, reg_date, reply_count, location_no
from	(
			select	ceil(rownum/#{contentsPerPage}) page, restaurant_no, restaurant_name, category, phone_no, address,
					description, picture_name, score, hits, theme, reg_date, reply_count, location_no
			from	(
						select	restaurant_no, restaurant_name, category, phone_no, address, description,
								picture_name, score, hits, theme, reg_date, reply_count, r.location_no
						from	restaurant r, location l
						
							r.location_no=l.location_no
							and l.building_name=#{buildingName}
							
								and l.floor=#{floor}
							
							
								and r.restaurant_name like '%'||#{searchWord}||'%'
							
						
						
							
								order by restaurant_name
							
							
								order by hits desc
							
							
								order by reply_count desc
							
							
								order by score desc
							
							
								order by restaurant_no desc
							
						
			)
		)
where	page=#{currentPage}

select count(restaurant_no)
from restaurant r, location l

	r.location_no=l.location_no
	and l.building_name=#{buildingName}
	
		and l.floor=#{floor}
	
	
		and r.restaurant_name like '%'||#{searchWord}||'%'

select count(restaurant_no) from restaurant
category=#{category}
and restaurant_name like '%'||#{searchWord}||'%'

mapper.update:
update restaurant set restaurant_name=#{restaurantName}, category=#{category}, phone_no=#{phoneNo},
	address=#{address}, description=#{description}, picture_name=#{pictureName}, theme=#{theme}, location_no=#{locationNo}
where restaurant_no=#{restaurantNo}
