mapper.update:
update  review
set		title=#{title},
		content=#{content},
		
		,picture_name=#{pictureName}
		
where	member_id=#{memberID}

mapper.select:
select review_no, title, content, picture_name, hits, recommend, reg_date, member_id
from (
		select ceil(rownum/#{contentsPerPage}) page, 
			   review_no, title, content, picture_name, hits, 
			   recommend, reg_date, member_id
		from (
				
				order by reg_date
	)
) 
where	page = #{currentPage}
