mapper.update:
update  review
set		title=#{title},
		content=#{content}
where	review_no=#{reviewNo} and member_id=#{memberId}

mapper.select:
update review
set hits = hits + 1
where review_no=#{reviewNo}

select count(*)
from review_recommend
where review_no=#{number} and member_id=#{id}

update review
set recommend = recommend + 1
where review_no=#{reviewNo}

select review_no, title, content, hits, recommend, reg_date, member_id, reply_count
from (
		select ceil(rownum/#{contentsPerPage}) page, 
			   review_no, title, content, hits, 
			   recommend, reg_date, member_id, reply_count
		from (
				
				order by reg_date desc
	)
) 
where	page = #{pageNo}

select review_no, title, content, hits, recommend, reg_date, member_id, reply_count from ( select ceil(rownum/#{contentsPerPage}) page, review_no, title, content, hits, recommend, reg_date, member_id, reply_count from ( select review_no, title, content, hits, recommend, reg_date, member_id, (select count(*) from REVIEW_REPLY where review_reply.review_no = review.review_no) reply_count from review
order by reg_date desc
order by recommend desc
order by hits desc
) ) where page = #{pageNo}
